import React, { useState } from "react";

const FilterTask = (props) => {
  const [selectedOptions, setSelectedOptions] = useState([]);

  const handleCheckboxChange = (e) => {
    const option = e.target.value;
    if (e.target.checked) {
      setSelectedOptions([...selectedOptions, option]);
    } else {
      setSelectedOptions(selectedOptions.filter((selected) => selected !== option));
    }
  };

  return (
    <div>
      <p className="txt">Filter Tasks</p>
      <div className="box">
        <input type="checkbox" className="box1" value="Panding" onChange={handleCheckboxChange} />
        <label>Panding</label>
        <input type="checkbox" className="box1" value="In Progress" onChange={handleCheckboxChange} />
        <label>In Progress</label>
        <input type="checkbox" value="Done" className="box1" onChange={handleCheckboxChange} />
        <label>Done</label>
      </div>
    </div>
  );
};

export default FilterTask;
