import React, { useState } from "react";
import "./Todo.css";
import AddTask from "./AddTask";
import FilterTask from "./FilterTask";
import ShowResult from "./ShowResult";

function Todo() {
  const [value, getValue] = useState("");
  const [items, setItems] = useState([]);
  const [filter, setFilter] = useState({
    pending: true,
    inProgress: true,
    done: true,
  });

  function recieveValue(newValue, checkBoxValue) {
    getValue(newValue);
    const newItem = { value: newValue, option: checkBoxValue, isChecked: true, id: Date.now() };
    setItems([...items, newItem]);
    console.log(items);
  }

  function handleFilterChange(updatedFilter) {
    setFilter(updatedFilter);
  }

  return (
    <div className="todo">
      <h1 className="heading">To do App</h1>
      <FilterTask filter={filter} onFilterChange={handleFilterChange} />
      <AddTask recieveValue={recieveValue} />
      <ShowResult items={items} filter={filter} />
    </div>
  );
}

export default Todo;
