import React, { useState } from "react";
import "./Todo.css";
import AddTask from "./AddTask";
import FilterTask from "./FilterTask";
import ShowResult from "./ShowResult";

function Todo() {
  const [value, getValue] = useState("");
  const [items, setItems] = useState([]);
  const [selectedFilters, setSelectedFilters] = useState([]);

  const handleFilterChange = (value, checked) => {
    if (checked) {
      setSelectedFilters((prevFilters) => [...prevFilters, value]);
    } else {
      setSelectedFilters((prevFilters) => prevFilters.filter((filter) => filter !== value));
    }
  };

  const recieveValue = (newValue, checkBoxValue) => {
    getValue(newValue);
    const newItem = { value: newValue, option: checkBoxValue, isChecked: true };
    setItems([...items, newItem]);
    console.log(items);
  };

  return (
    <div className="todo">
      <h1 className="heading">To do App</h1>
      <FilterTask handleFilterChange={handleFilterChange} />
      <AddTask recieveValue={recieveValue} />
      <ShowResult items={items} selectedFilters={selectedFilters} />
    </div>
  );
}

export default Todo;
