import React, { useState } from "react";

const FilterTask = (props) => {
  const [selectedFilters, setSelectedFilters] = useState([]);

  const handleFilterChange = (e) => {
    const filterValue = e.target.value;
    if (e.target.checked) {
      setSelectedFilters((prevFilters) => [...prevFilters, filterValue]);
    } else {
      setSelectedFilters((prevFilters) =>
        prevFilters.filter((filter) => filter !== filterValue)
      );
    }
  };

  return (
    <div>
      <p className="txt">Filter Tasks</p>
      <div className="box">
        <input
          type="checkbox"
          className="box1"
          value="Panding"
          onChange={handleFilterChange}
        />
        <label>Panding</label>
        <input
          type="checkbox"
          className="box1"
          value="In Progress"
          onChange={handleFilterChange}
        />
        <label>In Progress</label>
        <input
          type="checkbox"
          value="Done"
          className="box1"
          onChange={handleFilterChange}
        />
        <label>Done</label>
      </div>
      <ShowResult items={props.items} selectedFilters={selectedFilters} />
    </div>
  );
};

export default FilterTask;
