import React, { useState } from "react";

const FilterTask = (props) => {
  const [filter, setFilter] = useState({
    pending: false,
    inProgress: false,
    done: false
  });

  const handleFilterChange = (name) => {
    setFilter((prevFilter) => ({
      ...prevFilter,
      [name]: !prevFilter[name]
    }));
  };

  return (
    <div>
      <p className="txt">Filter Tasks</p>
      <div className="box">
        <input
          type="checkbox"
          id="pending"
          name="pending"
          value={filter.pending}
          onChange={() => handleFilterChange("pending")}
          defaultChecked
        />
        <label htmlFor="pending">Pending</label>
        <input
          type="checkbox"
          id="inProgress"
          name="inProgress"
          value={filter.inProgress}
          onChange={() => handleFilterChange("inProgress")}
          defaultChecked
        />
        <label htmlFor="inProgress">In Progress</label>
        <input
          type="checkbox"
          id="done"
          name="done"
          value={filter.done}
          onChange={() => handleFilterChange("done")}
          defaultChecked
        />
        <label htmlFor="done">Done</label>
      </div>
      {filter.done && (
        <div className="box">
          <select className="select">
            <option value="Done">Done</option>
          </select>
        </div>
      )}
    </div>
  );
};

export default FilterTask;
