import React, { useState } from "react";
import "./Todo.css";
import AddTask from "./AddTask";
import FilterTask from "./FilterTask";
import ShowResult from "./ShowResult";

function Todo() {
  const [value, getValue] = useState("");
  const [items, setItems] = useState([]);
  const [filters, setFilters] = useState({ pending: true, inProgress: true, done: true });

  function recieveValue(newValue, checkBoxValue) {
    getValue(newValue);
    const newItem = { value: newValue, option: checkBoxValue, isChecked: true };
    setItems([...items, newItem]);
    console.log(items);
  }

  function updateFilters(newFilters) {
    setFilters(newFilters);
  }
  function deleteTask(taskValue) {
    setItems(items.filter(item => item.value !== taskValue));
  }
  const filteredItems = items.filter(item => filters[item.option.toLowerCase()]);

  return (
    <div className="todo">
      <h1 className="heading">To do App</h1>
      <FilterTask filters={filters} updateFilters={updateFilters} />
      <AddTask recieveValue={recieveValue} />
      <ShowResult items={items} deleteTask={deleteTask} />
    </div>
  );
}

export default Todo;
