import React, { useState } from "react";
import "./Todo.css";
import AddTask from "./AddTask";
import FilterTask from "./FilterTask";
import ShowResult from "./ShowResult";

function Todo() {
  const [value, getValue] = useState("");
  const [items, setItems] = useState([]);
  const [showResult, setShowResult] = useState(true);

  function recieveValue(newValue, checkBoxValue) {
    getValue(newValue);
    const newItem = { value: newValue, option: checkBoxValue, isChecked: true };
    setItems([...items, newItem]);
    console.log(items);
    setShowResult(false);
  }

  function showResults() {
    setShowResult(true);
  }

  return (
    <div className="todo">
      <h1 className="heading">To do App</h1>
      <FilterTask items={items} showResults={showResults}/>
      <AddTask recieveValue={recieveValue} />
      {showResult && <ShowResult items={items} />}
    </div>
  );
}

export default Todo;
