import React, { useState } from "react";
import "./Todo.css";
import AddTask from "./AddTask";
import FilterTask from "./FilterTask";
import ShowResult from "./ShowResult";

function Todo() {
  const [value, getValue] = useState("");
  const [items, setItems] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState([]);

  const recieveValue = (newValue, checkBoxValue) => {
    getValue(newValue);
    const newItem = { value: newValue, option: checkBoxValue, isChecked: true };
    setItems([...items, newItem]);
    console.log(items);
  };

  return (
    <div className="todo">
      <h1 className="heading">To do App</h1>
      <FilterTask selectedOptions={selectedOptions} setSelectedOptions={setSelectedOptions} />
      <AddTask recieveValue={recieveValue} />
      <ShowResult items={items} selectedOptions={selectedOptions} />
    </div>
  );
}


export default Todo;
